local i = {}

function i.IsLatin(frame)
    local txt = frame.args[1];
    if txt == '' then
        return '';
    end

    local len = mw.ustring.len(txt);
    local pos = 1;
    while ( pos <= len ) do
        charval = mw.ustring.codepoint(mw.ustring.sub(txt, pos))
        -- note 8364 is the â‚¬ symbol
        if charval>687 and charval~=8364 then
            return "no";
        end 
        pos = pos + 1;
    end
    return "yes";
end

return i;
